import React, {useEffect, useRef, useState} from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Chart from 'chart.js/auto';
import { ThemeProvider, createTheme } from '@mui/material/styles';

const Dashboard: React.FC = () => {
    const [userData, setUserData] = useState<number>(1000); // Mock user data
    const [eventData, setEventData] = useState<number>(50); // Mock event data
    const [graphQuery, setGraphQuery] = useState<string>(''); // State for the graph query
    const chartContainerRef = useRef<HTMLDivElement | null>(null);
    const myChartRef = useRef<Chart | null>(null);

    //  const graphData = [60, 40, 80, 30, 70];

    const theme = createTheme({
        palette: {
            primary: {
                main: '#c9edf3',
            },
            secondary: {
                main: '#ffe4df'
            }
        },
    });
    const handleGenerateGraph = (data: number, label: string, color: string) => {
        // Check if myChart exists, and destroy it if it does
        if (myChartRef.current !== null) {
            myChartRef.current.destroy();
        }

        // Your code to generate or fetch data and create a new chart goes here
        const graphData = {
            labels: [label],
            datasets: [
                {
                    label,
                    data: [data],
                    backgroundColor: [color], // Add your own colors
                },
            ],
        };

        // Create a new canvas element
        const canvas = document.createElement('canvas');
        canvas.width = 400; // Set the width and height as per your requirement
        canvas.height = 400;

        // Append the canvas to the chart container
        if (chartContainerRef.current) {
            chartContainerRef.current.innerHTML = ''; // Clear previous charts
            chartContainerRef.current.appendChild(canvas);
        }

        // Create a new chart
        const ctx = canvas.getContext('2d');
        if (ctx) {
            myChartRef.current = new Chart(ctx, {
                type: 'bar',
                data: graphData,
                // ... other chart configurations
            });
        }
    };

    const handleCloseChart = () => {
        // Check if myChart exists, and destroy it if it does
        if (myChartRef.current !== null) {
            myChartRef.current.destroy();
        }

        // Clear the chart container
        if (chartContainerRef.current) {
            chartContainerRef.current.innerHTML = '';
        }
    };

    useEffect(() => {
        // Initial chart generation with mock data
        handleGenerateGraph(userData, 'Registered Users', 'blue');
    }, [userData]);


    return (
        <ThemeProvider theme={theme}>
        <Container>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <Typography variant="h4">Dashboard</Typography>
                </Grid>
                <Grid item xs={12}>
                    <Button variant="contained" color="primary" onClick={handleGenerateGraph}>
                        Generate Chart
                    </Button>
                    <Button variant="contained" color="secondary" onClick={handleCloseChart}>
                        Close Chart
                    </Button>
                </Grid>
                <Grid item xs={12}>
                    <div ref={chartContainerRef} style={{ width: '20rem', height: '16rem' }}></div>
                </Grid>
            </Grid>
        </Container>
        </ThemeProvider>
    );
};

export default Dashboard;
