import React, { useEffect, useRef, useState } from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Chart from 'chart.js/auto';
import { ThemeProvider, createTheme } from '@mui/material/styles';

interface ChartData {
    label: string;
    data: number;
    color: string;
}

const Dashboard: React.FC = () => {
    const [userData, setUserData] = useState<number>(1000);
    const [eventData, setEventData] = useState<number>(50);
    const [chartData, setChartData] = useState<ChartData[]>([]);
    const chartContainerRef = useRef<HTMLDivElement | null>(null);
    const myChartRefs = useRef<Array<Chart | null>>([]);

    const theme = createTheme({
        palette: {
            primary: {
                main: '#c9edf3',
            },
            secondary: {
                main: '#ffe4df',
            },
        },
    });

    const handleGenerateGraph = (data: number, label: string, color: string) => {
        // Create a new chart data object
        const newChartData: ChartData = { label, data, color };

        // Update the chart data array
        setChartData((prevChartData) => [...prevChartData, newChartData]);

        // Clear the chart container
        if (chartContainerRef.current) {
            chartContainerRef.current.innerHTML = '';
        }

        // Create a new chart for each data set
        myChartRefs.current = newChartData.map((dataSet, index) => {
            const canvas = document.createElement('canvas');
            canvas.width = 400; // Set the width and height as per your requirement
            canvas.height = 400;

            if (chartContainerRef.current) {
                chartContainerRef.current.appendChild(canvas);
            }

            const ctx = canvas.getContext('2d');
            if (ctx) {
                myChartRefs.current[index] = new Chart(ctx, {
                    type: 'bar',
                    data: {
                        labels: [dataSet.label],
                        datasets: [
                            {
                                label: dataSet.label,
                                data: [dataSet.data],
                                backgroundColor: [dataSet.color],
                            },
                        ],
                    },
                });
            }
        });
    };

    const handleCloseChart = () => {
        // Destroy all existing charts
        myChartRefs.current.forEach((chart) => {
            if (chart) {
                chart.destroy();
            }
        });

        // Clear the chart data array
        setChartData([]);

        // Clear the chart container
        if (chartContainerRef.current) {
            chartContainerRef.current.innerHTML = '';
        }
    };

    useEffect(() => {
        // Initial chart generation with mock data
        handleGenerateGraph(userData, 'Registered Users', 'blue');
    }, [userData]);

    return (
        <ThemeProvider theme={theme}>
            <div>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="h6">Registered Users</Typography>
                        <Typography variant="subtitle1">{userData}</Typography>
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="h6">Event Data</Typography>
                        <Typography variant="subtitle1">{eventData}</Typography>
                    </Grid>
                </Grid>

                <button
                    onClick={() => handleGenerateGraph(userData, 'Registered Users', 'blue')}
                    style={{ background: '#ffe4df' }}
                >
                    Generate Users Chart
                </button>
                <button
                    onClick={() => handleGenerateGraph(eventData, 'Event Data', 'green')}
                    style={{ background: '#ffe4df' }}
                >
                    Generate Event Chart
                </button>
                <button onClick={handleCloseChart} style={{ background: '#c9edf3' }}>
                    Close Chart
                </button>
                <div ref={chartContainerRef} style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}></div>
            </div>
        </ThemeProvider>
    );
};

export default Dashboard;
