import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Sidebar from '../components/Sidebar'; // Create Sidebar component
import './Dashboard.css'; // Import the corresponding CSS file

// Define your dashboard pages/components
const DashboardHome = () => <div>Dashboard Home</div>;
const UsersPage = () => <div>Users Page</div>;
const SettingsPage = () => <div>Settings Page</div>;

const Dashboard: React.FC = () => {
    return (
        <Router>
            <div style={{ display: 'flex' }}>
                {/* Sidebar */}
                <Sidebar />

                <div style={{ flexGrow: 1, height: '100vh', overflow: 'auto' }}>
                    {/* App Bar */}
                    <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
                        <Toolbar>
                            <Typography variant="h6" noWrap>
                                Admin Dashboard
                            </Typography>
                        </Toolbar>
                    </AppBar>

                    {/* Main Content */}
                    <Container maxWidth="lg" sx={{ marginTop: 4, padding: 2 }}>
                        <Switch>
                            <Route path="/" exact component={DashboardHome} />
                            <Route path="/users" component={UsersPage} />
                            <Route path="/settings" component={SettingsPage} />
                        </Switch>
                    </Container>
                </div>
            </div>
        </Router>
    );
};

export default Dashboard;
